"""**Text Pre-processing - Turn alphabets lower case, numbers, HTML tags, web pages, mentions, hashtags and contractions**"""

def pre_process(text):
    text = text.lower()
    text = re.sub('[0-9]+', '', text)
    text = re.sub("<!--?.*?-->","",text)
    text = re.sub(r'(http|ftp|https)://([\w_-]+(?:(?:\.[\w_-]+)+))([\w.,@?^=%&:/~+#-]*[\w@?^=%&/~+#-])?','',text)
    text = re.sub(r'pic.twitter.com/[\w_-]+','',text)
    text = re.sub(r'(@|#)[\w_-]+','',text)
    text = re.sub(r"can't","cannot",text)
    text = re.sub(r"can't've","cannot have",text)
    text = re.sub(r"'cause","because",text)
    text = re.sub(r"could've","could have",text)
    text = re.sub(r"couldn't","could not",text)
    text = re.sub(r"couldn't've","could not have",text)
    text = re.sub(r"didn't","did not",text)
    text = re.sub(r"doesn't","does not",text)
    text = re.sub(r"doesnt","does not",text)
    text = re.sub(r"don't","do not",text)
    text = re.sub(r"hadn't","had not",text)
    text = re.sub(r"hadn't've","had not have",text)
    text = re.sub(r"hasn't","has not",text)
    text = re.sub(r"haven't","have not",text)
    text = re.sub(r"he'd","he would",text)
    text = re.sub(r"he'd've","he would have",text)
    text = re.sub(r"he'll","he will",text)
    text = re.sub(r"he'll've","he he will have",text) 
    text = re.sub(r"he's","he is",text)
    text = re.sub(r"what's", "what is ", text)
    text = re.sub(r"mustn't've","must not have",text)
    text = re.sub(r"ain't","is not",text)
    text = re.sub(r"\â€™s", " ", text) 
    text = re.sub(r"\'s", " ", text)
    text = re.sub(r"\'ve", " have ", text)
    text = re.sub(r"\â€™ve", " have ", text)
    text = re.sub(r"can't", "can not ", text)
    text = re.sub(r"canâ€™t", "can not ", text)
    text = re.sub(r"n't", " not ", text)
    text = re.sub(r"nâ€™t", " not ", text)
    text = re.sub(r"i'm", "i am ", text)
    text = re.sub(r"iâ€™m", "i am ", text)
    text = re.sub(r"\'re", " are ", text)
    text = re.sub(r"she'll", " she will ", text)
    text = re.sub(r"coz", " because ", text)
    text = re.sub(r"now's", " now is ", text)
    text = re.sub(r"you're", " you are ", text)
    text = re.sub(r"\â€™re", " are ", text)
    text = re.sub(r"\'d", " would ", text)
    text = re.sub(r"\â€™d", " would ", text)
    text = re.sub(r"\'ll", " will ", text)
    text = re.sub(r"\â€™ll", " will ", text)
    text = re.sub(r"\'scuse", " excuse ", text)
    text = re.sub(r"\â€™scuse", " excuse ", text)
    
    return text

