def clean_text(s):
    s = s.lower()
    s = utils.to_unicode(s)
    for f in filters:
        s = f(s)
    return s

"""**Space out emojis**"""

def space_out_emojis(s):
  for c in s:
    if(c in emoji.UNICODE_EMOJI):
      spaced_c = " "+c+" "
      s = s.replace(c,spaced_c)
  return s

"""**Lemmatize**"""

nltk.download('averaged_perceptron_tagger')
nltk.download('wordnet')

def get_wordnet_pos(word):
    tag = nltk.pos_tag([word])[0][1][0].upper()
    tag_dict = {"J": wordnet.ADJ,
                "N": wordnet.NOUN,
                "V": wordnet.VERB,
                "R": wordnet.ADV}

    return tag_dict.get(tag, wordnet.NOUN)

lemmatizer = WordNetLemmatizer() 

def lemmatize_sentence(s):
    s = s.split()
    lemmatized_tokens = [lemmatizer.lemmatize(w, get_wordnet_pos(w)) for w in s]
    lemmatized_string = ' '.join([str(elem) for elem in lemmatized_tokens])

    return lemmatized_string

"""**Remove Stop Words**"""

def remove_stop_words(s):
    stopwords = ['i', 'me', 'my', 'myself', 'we', 'our', 'ours', 'ourselves', 'you', "you're", "you've", "you'll", "you'd", 'your', 'yours', 'yourself', 'yourselves', 'he', 'him', 'his', 'himself', 'she', "she's", 'her', 'hers', 'herself', 'it', "it's", 'its', 'itself', 'they', 'them', 'their', 'theirs', 'themselves', 'this', 'that', "that'll", 'these', 'those', 'am', 'is', 'are', 'was', 'were', 'be', 'been', 'being', 'have', 'has', 'had', 'having', 'did', 'doing', 'a', 'an', 'the', 'because', 'as', 'until', 'while', 'of', 'at', 'by', 'for', 'with', 'about', 'between', 'into', 'through', 'above', 'below', 'to', 'from', 'up', 'down', 'in', 'out', 'on', 'off', 'over', 'under', 'again', 'further', 'then', 'once', 'here', 'there', 'all', 'any', 'both', 'each', 'few', 'more', 'most', 'other', 'some', 'such', 'no', 'nor', 'only', 'own', 'same', 'so', 'than', 'too', 'very', 'can', 'will', 'just', 'should', 'now']
    #stopwords.extend(['corona', 'covid','quarantine','lockdown'])
    s = s.split()
    tokens_without_sw = [word for word in s if not word in stopwords]
    string_without_sw = ' '.join([str(elem) for elem in tokens_without_sw])

    return string_without_sw

