"""**Remove other languages**"""

def remove_otherLanguageCharacters(s):
    s = s.split()
    tokens = ""
    tokens = [word for word in s if ((word[0] in emoji.UNICODE_EMOJI) or (ord(u'\u0900') <= ord(word[0]) <= ord(u'\u097F')) or (ord(u'\u0041') <= ord(word[0]) <= ord(u'\u005A')) or (ord(u'\u0061') <= ord(word[0]) <= ord(u'\u007A')))]
    string = ' '.join([str(elem) for elem in tokens])

    return string

"""**Deal with Emoji Repetition**"""

def strip_emojis(s):
	s = s.split()
	tokens = []

	for word in s :
		if word in emoji.UNICODE_EMOJI and word not in tokens:
			tokens.append(word)
		elif word not in emoji.UNICODE_EMOJI:
			tokens.append(word)
	string = ' '.join([str(elem) for elem in tokens])

	return string

