def emoji_to_tags(s):
    s = s.split()
    tokens = []
    # tokens = [emoji_to_tag[word] if ((word in emoji_to_tag.keys())) else word for word in s ]

    for word in s :
      if word in emoji.UNICODE_EMOJI :
              try :
                tokens.append(emoji_to_tag[word])
              except :
                tokens.append("")
      else :
        tokens.append(word)    
    
    string = ' '.join([str(elem) for elem in tokens])

    return string

